jQuery(document).ready(function(a) {
    jQuery( "#datepicker1" ).datepicker({
        dateFormat: "yy-mm-dd",
      changeMonth: true,//this option for allowing user to select month
      changeYear: true //this option for allowing user to select from year range
    });
    jQuery( "#datepicker2" ).datepicker({
        dateFormat: "yy-mm-dd",
      changeMonth: true,//this option for allowing user to select month
      changeYear: true //this option for allowing user to select from year range
    });
    "use strict";
     a("#v_start_date").datepicker({
        dateFormat: "yy-mm-dd",
        numberOfMonths: 1,
        showButtonPanel: !0,
        showOn: "button",
        buttonImage: woocommerce_review_csv_import_params.calendar_icon,
        buttonImageOnly: !0
    }),a("#v_end_date").datepicker({
        dateFormat: "yy-mm-dd",
        numberOfMonths: 1,
        showButtonPanel: !0,
        showOn: "button",
        buttonImage: woocommerce_review_csv_import_params.calendar_icon,
        buttonImageOnly: !0
    }),
        a("#rev_enable_ftp_ie").click(function () {
        if (this.checked) {
            a("#rev_export_section_all").show();
        }else{
            a("#rev_export_section_all").hide();
        }
    });
    a("select[name=rev_auto_export]").change(function() {
        if("Disabled" === a(this).val()){
            a(".rev_export_section").hide();
        }else{
            a(".rev_export_section").show();
        }
    });
    
    if(woocommerce_review_csv_cron_params.rev_enable_ftp_ie != 1){
        a("#rev_export_section_all").hide();
    };
    if(woocommerce_review_csv_cron_params.rev_auto_export === 'Disabled'){
        a(".rev_export_section").hide();
    };
    a("select[name=rev_auto_import]").change(function() {
        if("Disabled" === a(this).val()){
            a(".rev_import_section").hide();
        }else{
            a(".rev_import_section").show();
        }
    })
    if(woocommerce_review_csv_cron_params.rev_auto_import === 'Disabled'){
        a(".rev_import_section").hide();
    }
        // Listen for click on toggle checkbox
        a('#selectall').click(function(event) {   
                // Iterate each checkbox
               a(':checkbox').each(function() {
                    this.checked = true;
                });
        });
        a('#unselectall').click(function(event) {   
                // Iterate each checkbox
               a(':checkbox').each(function() {
                    this.checked = false;
                });
        });
});